enum Instruction: Character {
    case up = "("
    case down = ")"
}

var floor = 0
for (index, rawInstruction) in rawInstructions.characters.enumerated() {
    guard let instruction = Instruction(rawValue: rawInstruction) else {
        continue
    }

    switch instruction {
    case .up:
        floor += 1
    case .down:
        floor -= 1
    }

    if floor == -1 {
        print("Floor -1 @\(index)")
    }

}

print ("Floor: \(floor)")
